package com.example.book_store.listimport android.util.Logimport androidx.lifecycle.ViewModelimport com.example.book_store.api.RetrofitInstanceimport models.NewBookBodyclass CreateDeleteUpdBookViewModel():ViewModel() {    suspend fun createNewBook(        title: String,        authorName: String,        price: Double,        bookAvailable: Int,        genre: String,        publishedDate: String,        desc: String    ) {        try {            val newBook = RetrofitInstance.bookService.addBook(                NewBookBody(                    title = title,                    price = price.toDouble(),                    type = genre,                    date = publishedDate,                    age = bookAvailable.toInt(),                    phone = authorName,                    description = desc                )            )        } catch (ex: Exception) {            ex.printStackTrace()        }    }    suspend fun updateBookById(        id: Int,        title: String,        authorName: String,        price: Double,        bookAvailable: Int,        genre: String,        publishedDate: String,        desc: String    ) {        try {            Log.d("id",id.toString())            val newBook = RetrofitInstance.bookService.updateBookByID(                id,                NewBookBody(                    title = title,                    price = price,                    type = genre,                    date = publishedDate,                    age = bookAvailable,                    phone = authorName,                    description = desc                )            )        } catch (ex: Exception) {            ex.printStackTrace()        }    }    suspend fun deleteBookByID(        id: Int,    ) {        try {            val newBook = RetrofitInstance.bookService.deleteBookByID(                id,            )        } catch (ex: Exception) {            ex.printStackTrace()        }    }}